static uint32_t get_timeout(void) {
    switch (timeout_mode) {
        default:
        case 0:
            return 90000;  // 1m30s
        case 1:
            return 120000;  // 2m
        case 2:
            return 300000;  // 5m
        case 3:
            return 60000;  // 1m
    }
}

// Bongo frames and variables
uint8_t bongo_current_idle_frame     = 0;
uint8_t bongo_current_pre_idle_frame = 0;
uint8_t bongo_current_tap_frame      = 0;
uint8_t bongo_current_caps_frame     = 0;
uint8_t bongo_state_tap = 0;
uint32_t anim_timer     = 0;

static char* get_wpm_str(void) {
    static char wpm_str[5] = "";
    if (wpm() > 0) {
        sprintf(wpm_str, "%03d", wpm());
    } else {
        sprintf(wpm_str, "wpm");
    }
    return wpm_str;
}

void draw_bongo_dynamic(void) {
    uint8_t mod_state = get_mods();

    // assumes 1 frame prep stage
    // mode 0 = default, mode 1 = pre idle
    void animation_phase(uint8_t mode) {
        if ((mod_state & MOD_MASK_SHIFT)) {
            bongo_current_caps_frame = (bongo_current_caps_frame + 1) % BONGO_CAPS_FRAMES;
            oled_write_raw_P(bongo_filled_caps[abs((BONGO_CAPS_FRAMES - 1) - bongo_current_caps_frame)] , DEFAULT_ANIM_SIZE);
            return;
        }
        bongo_current_caps_frame = 0;
        if (mode == 1) {
            bongo_current_pre_idle_frame = (bongo_current_pre_idle_frame + 1) % BONGO_PRE_IDLE_FRAMES;
            oled_write_raw_P(bongo_filled_pre_idle[abs((BONGO_PRE_IDLE_FRAMES - 1) - bongo_current_pre_idle_frame)] , DEFAULT_ANIM_SIZE);
        } else {
            bongo_current_idle_frame = (bongo_current_idle_frame + 1) % BONGO_IDLE_FRAMES;
            oled_write_raw_P(bongo_filled_idle[abs((BONGO_IDLE_FRAMES - 1) - bongo_current_idle_frame)] , DEFAULT_ANIM_SIZE);
        }
    }

    if ((mod_state & MOD_MASK_CTRL) && bongo_state_tap != 1) {
        oled_write_raw_P(bongo_filled_hiding[0] ,DEFAULT_ANIM_SIZE);
        anim_timer      = timer_read32();
        bongo_state_tap = 2;
    } else if ((mod_state & MOD_MASK_GUI) && bongo_state_tap != 1) {
        oled_write_raw_P(bongo_filled_blushing[0] , DEFAULT_ANIM_SIZE);
        anim_timer      = timer_read32();
        bongo_state_tap = 2;
    } else if (bongo_state_tap == 1) {
        if ((mod_state & MOD_MASK_SHIFT)) {
            oled_write_raw_P(bongo_filled_caps[1] , DEFAULT_ANIM_SIZE);
        } else if (mod_state & MOD_MASK_CTRL) {
            bongo_current_tap_frame = (bongo_current_tap_frame + 1) % BONGO_TAP_FRAMES;
            oled_write_raw_P(bongo_filled_hiding_tap[abs((BONGO_TAP_FRAMES - 1) - bongo_current_tap_frame)] , DEFAULT_ANIM_SIZE);
        } else if (mod_state & MOD_MASK_GUI) {
            bongo_current_tap_frame = (bongo_current_tap_frame + 1) % BONGO_TAP_FRAMES;
            oled_write_raw_P(bongo_filled_blushing_tap[abs((BONGO_TAP_FRAMES - 1) - bongo_current_tap_frame)] , DEFAULT_ANIM_SIZE);
        } else if (wpm() > 140) {
            bongo_current_tap_frame = (bongo_current_tap_frame + 1) % BONGO_TAP_FRAMES;
            oled_write_raw_P(bongo_filled_tap_cute[abs((BONGO_TAP_FRAMES - 1) - bongo_current_tap_frame)] , DEFAULT_ANIM_SIZE);
        } else {
            bongo_current_tap_frame = (bongo_current_tap_frame + 1) % BONGO_TAP_FRAMES;
            oled_write_raw_P(bongo_filled_tap[abs((BONGO_TAP_FRAMES - 1) - bongo_current_tap_frame)] , DEFAULT_ANIM_SIZE);
        }
        anim_timer      = timer_read32();
        bongo_state_tap = 2;
    } else if (timer_elapsed32(anim_timer) < 3000 && bongo_state_tap == 2) {
        if ((mod_state & MOD_MASK_SHIFT)) {
            oled_write_raw_P(bongo_filled_caps[0] , DEFAULT_ANIM_SIZE);
        } else {
            oled_write_raw_P(bongo_filled_prep[0] , DEFAULT_ANIM_SIZE);
        }
    } else {
        bongo_state_tap = 0;
        if (get_timeout() + timer_elapsed32(oled_sleep_timer) > PRE_SLEEP_TIMEOUT && !(mod_state & MOD_MASK_SHIFT)) {
            if (timer_elapsed32(anim_timer) > BONGO_SLEEP_FRAME_DURATION) {
                anim_timer = timer_read32();
                animation_phase(1);
            }
        } else if (timer_elapsed32(anim_timer) > BONGO_IDLE_FRAME_DURATION) {
            anim_timer = timer_read32();
            animation_phase(0);
        }
    }

    // draw_rectangle(108, 6, 20, 11, false);
    if (biton32(layer_state) > 0) {
        oled_set_cursor(19, 2);
        oled_write_P(PSTR("L"), false);
        oled_write_char(get_highest_layer(layer_state) + 0x30, false);
    }

    if ((mod_state & MOD_MASK_SHIFT)) {
        oled_set_cursor(0, 2);
        oled_write_P(PSTR("CAPS"), false);
    }


        oled_set_cursor(18, 3);
        oled_write(get_wpm_str(), false);

}

